import { Button } from "@mui/material";
import React, { useState } from "react";
import { logout } from "../../utils/auth";

import { FolderTree } from "../FolderTree/FolderTree";
import Researchers from "./Researchers";
import Users from "./Users";
import Collaborators from "./Collaborator";

const Sidebar = ({
  handleClick,
  treeData,
  isAdmin = false,
  isResearcer = false,
  errorMsg = "Provide treeData",
  disableRightClick = false,
  disableFileAndFolderAddition = false,
}) => {
  const [sidebarOpen, setSideBarOpen] = useState(false);
  const handleViewSidebar = () => {
    setSideBarOpen(!sidebarOpen);
  };
  return (
    <div className="sidebar sticky top-0 ">
      <div>
        <div style={{ paddingBottom: "40px" }}>
          <div className="folderTree">
            <FolderTree
              treeData={treeData}
              handleClick={handleClick}
              isAdmin={isAdmin}
              isResearcer={isResearcer}
              errorMsg={errorMsg}
              disableRightClick={disableRightClick}
              disableFileAndFolderAddition={disableFileAndFolderAddition}
            />
            {isAdmin ? (
              <div className="mt-5 flex-col-center gap-4 items-start">
                <Researchers />
                <Users />
                <Collaborators />
              </div>
            ) : isResearcer ? (
              <div className="mt-5 flex-col-center gap-4 items-start">
                <Collaborators />
              </div>
            ) : null}
          </div>
          <Button
            variant="outlined"
            size="small"
            onClick={() => logout()}
            sx={{
              textTransform: "none",
              bottom: 40,
              left: 10,
              position: "absolute",
            }}
            style={{
              color: "#FFEB3B",
            }}
          >
            Logout
          </Button>
        </div>
      </div>
    </div>
  );
};
export default Sidebar;
