import React, { useState, createContext } from "react";
import Card from "../../components/Card/Card";
// import ipImg from "../../images/cardIp.png";
import "./InputText.css";
import { Name } from "../../components/FolderTree/FolderTree";
import StorageUnit from "./StorageUnit";
import Logout from "./Logout";
const share = createContext();
const SomeD = () => {
  return (
    <div>
      <Name.Consumer>
        {(data) => {
          console.log(data);
          <RightContent data={data} />;
        }}
      </Name.Consumer>
    </div>
  );
};

const RightContent = ({ content = {} }, props) => {
  console.log(props.data);
  function TextInput({ type = "text", label }) {
    const [value, setValue] = useState("");
    function handleChange(e) {
      setValue(e.target.value);
    }
    return (
      <div className="input-container">
        <input type={type} value={value} onChange={handleChange} />
        <label className={value && "filled"}>{label}</label>
      </div>
    );
  }

  const handleUploadChange = (e) => {
    // console.log(e.target.files);
    const files = e.target.files;
    let reader = new FileReader();
    reader.readAsDataURL(files[0]);

    // reader.onload(e) => {}
  };

  const getPath = (object, search) => {
    if (object.id === search) return [object.module];
    else if (object.children || Array.isArray(object)) {
      let children = Array.isArray(object) ? object : object.children;
      for (let child of children) {
        let result = getPath(child, search);
        if (result) {
          if (object.id) result.unshift(object.module);
          return result;
        }
      }
    }
  };

  // const data = treeData.children;
  // console.log(getPath(data, "1670364793246"));

  return (
    <div
      className="mt-4"
      style={{
        marginLeft: "100px",
        marginTop: "100px",
        display: "block",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <Logout style={{ marginLeft: "200px" }} />
      {/* <share.Consumer>
        {(data) => {
          console.log(data);
        }}
      </share.Consumer> */}
      {/* {content.module === "Storage Unit" && (
        <StorageUnit handleUploadChange={handleUploadChange} />
      )} */}
      {/* Stringify was here */}
      {/* {console.log(content.id)} */}
      {content.module === "Storage Unit" ? (
        <div>
          <StorageUnit
            handleUploadChange={handleUploadChange}
            style={{
              display: "flex",
              justifyContent: "center",
              width: "10px",
            }}
          />
        </div>
      ) : (
        <Card
          style={{
            display: "flex",
            justifyContent: "center",
            marginLeft: "20px",
          }}
        >
          <div
            className="h-[80vh] w-[40vw] flex-col-center gap-10 d-block"
            style={{ backgroundColor: "#C29591" }}
          >
            <p style={{ color: "white" }}>{content.module}</p>
            <form>
              <TextInput label="IP Address" />
              <TextInput label="Instrument Model" />
              <TextInput label="Default Gateway" />
            </form>
            <p style={{ color: "white" }}>
              Status: <b style={{ color: "green" }}>Active</b>
            </p>
          </div>
        </Card>
      )}

      {/* <pre>{JSON.stringify(content, undefined, 4)}</pre> */}
    </div>
  );
};

export { SomeD };
export default RightContent;
