import React from "react";
import { useState } from "react";
import { ContextMenu, ContextMenuTrigger, MenuItem } from "react-contextmenu";
// Rendering individual images
const Image = ({ image, handleDelete, handleClick = () => {} }) => {
  return (
    <div className="file-item">
      <img alt={`img - ${image.id}`} src={image.src} className="file-img" />
    </div>
  );
};

// ImageList Component//
const ImageGride = ({ images }) => {
  const [img, setImg] = useState([]);
  const removeElement = (id) => {
    const newImgs = images.filter((item) => item.id !== id);
    // console.log(newImgs);
    setImg(newImgs);
  };

  // render each image by calling Image component
  const renderImage = (image, index) => {
    return <Image image={image} key={`${image.id}-image`} />;
  };
  // Return the list of files//
  return (
    <div>
      <section className="file-list">
        {images.map(renderImage)}
        {images.forEach((element) => {
          console.log(element);
        })}
        <button onClick={() => removeElement(images.id)}>Remove</button>
      </section>
      {/* {images.map((x) => x.id)} */}
      {console.log(images)}
    </div>
  );
};

export default ImageGride;
