import React from "react";
import { useState } from "react";
// Rendering individual images
const Image = ({ image }) => {
  return (
    <div className="file-item">
      <img alt={`img - ${image.id}`} src={image.src} className="file-img" />
    </div>
  );
};

// ImageList Component//
const ImageGride = ({ images }) => {
  const [img, setImg] = useState(images);
  const removeElement = (id) => {
    const newFruits = images.filter((fruit) => fruit.id !== id);
    setImg(newFruits);
  };
  // render each image by calling Image component
  const renderImage = (image, index) => {
    return <Image image={image} key={`${image.id}-image`} />;
  };
  // Return the list of files//
  return (
    <div>
      <section className="file-list">{images.map(renderImage)}</section>
      {/* {images.map((x) => x.id)} */}

      {/* <button onClick={() => removeElement(img.id)}>{images.name}</button> */}
    </div>
  );
};

export default ImageGride;
